vec4 Effect2(vec2 uv) {
    // Base recursive pattern with time-based color cycling
    vec3 baseColor = hsl(iTime * 0.5, 1.0, 0.5);
    vec3 finalColor = vec3(0.0);

    // Recursive pattern generation
    for(float i = 0.0; i < 4.0; i++) {
        // Scale and rotate coordinates
        uv *= 2.0;
        uv += sin(iTime + i) * 0.5;

        // Add noise and turbulence
        float n = noise(uv);
        float t = turbulent(uv);

        // Create sine wave transitions
        vec3 color = hsl2rgb(vec3(
            (iTime + sin(length(uv))) * 0.1,
            1.0,
            0.5
        ));

        // Combine colors and patterns
        finalColor += color * (n + t) * 0.5;
    }

    return vec4(finalColor, 1.0);
}
