vec4 Effect2(vec2 uv) {
    vec2 st = uv * 10.0;

    float time = iTime * 0.5;

    // Create a base pattern using sine waves
    float patternX = sin(st.x + time) * 0.5 + 0.5;
    float patternY = sin(st.y + time * 0.7) * 0.5 + 0.5;

    // Add noise and turbulence for more complexity
    vec2 noiseUV = st * 0.1;
    float noiseVal = noise(noiseUV);
    float turbulentVal = turbulent(noiseUV);

    // Combine pattern and noise
    float combined = (patternX + patternY) * 0.5 + noiseVal * 0.3 + turbulentVal * 0.2;

    // Create color using HSL to HSV conversion
    vec3 hslColor = vec3(
        sin(time + st.x * 2.0) * 0.5 + 0.5,   // Hue
        0.7 + noiseVal * 0.3,                  // Saturation
        0.5 + turbulentVal * 0.2               // Lightness
    );

    vec3 rgbColor = hsl2rgb(hslColor);

    // Add some depth using sine functions
    float depth = sin(time + st.x * 0.5) * 0.5 + 0.5;
    rgbColor *= depth;

    return vec4(rgbColor, 1.0);
}
