vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Wave pattern for red channel
    float r = sin(uv.x * 10.0 + iTime) * 0.5 + 0.5;
    r += sin(uv.y * 5.0 + iTime * 2.0) * 0.3;

    // Wave pattern for green channel
    float g = sin(uv.y * 20.0 + iTime) * 0.4 + 0.6;
    g += cos(uv.x * 15.0 + iTime * 3.0) * 0.2;

    // Wave pattern for blue channel
    float b = sin((uv.x + uv.y) * 8.0 + iTime * 4.0) * 0.6 + 0.4;
    b += cos(uv.x * 3.0 + uv.y * 5.0 + iTime) * 0.2;

    color.r = r;
    color.g = g;
    color.b = b;

    return vec4(color, 1.0);
}
