vec4 Effect2(vec2 uv) {
    // Create hexagonal grid pattern
    vec2 q = floor(uv * 5.0 + 0.5);

    // Convert UV to polar coordinates relative to center of hexagon
    vec2 p = uv - (q + 0.5)/5.0;
    float r = length(p) * 16.0;
    float theta = atan2(p.y, p.x) + iTime;

    // Create rotating hexagonal pattern with noise perturbation
    float d = sin(theta * 3.0 - iTime) * 0.5 + 0.5;
    r += noise(vec3(q + iTime, 0.0)) * 0.2;

    // Calculate color based on angle and time
    vec3 color = hsl2rgb(vec3(theta * 180.0 / PI + iTime * 60.0, 1.0, 0.5));

    // Create pulsing edge effect
    float edge = sin(r - iTime * 4.0) * 0.5 + 0.5;
    color *= edge;

    return vec4(color, 1.0);
}
