vec4 Effect2(vec2 uv) {
    float t = iTime * 0.1;

    // Create a rotating pattern with noise
    vec2 pos = uv + sin(t * 5.0) * 0.1;
    pos = (pos - 0.5) * mat2(cos(t), sin(t), -sin(t), cos(t)) * 0.5 + 0.5;

    // Add some movement and scale
    vec2 scaled = pos * 4.0 + t;

    // Use noise to create a turbulent effect
    float n1 = turbulent(vec3(scaled.x, scaled.y, t));
    float n2 = turbulent(vec3(scaled.x * 0.5, scaled.y * 0.5, t * 0.5));

    // Combine noise values with UV coordinates
    vec2 finalPos = uv + (n1 - 0.5) * 0.2;
    finalPos = finalPos * mat2(cos(t * 2.0), sin(t * 2.0), -sin(t * 2.0), cos(t * 2.0));

    // Create color from noise values
    vec3 color = hsl2rgb(vec3(
        (n1 + n2) * 0.5,
        0.7,
        0.6 + n1 * 0.4
    ));

    return vec4(color, 1.0);
}
