vec4 Effect2(vec2 uv) {
    // Create base noise pattern
    float n = noise(uv * 10.0 + vec2(0.0, iTime));

    // Add sine wave patterns
    float s1 = sin(uv.x * 20.0 - iTime);
    float s2 = sin(uv.y * 15.0 + iTime);

    // Combine noise and waves with different frequencies
    vec3 color;
    color.r = n * 0.5 + s1 * 0.5;
    color.g = n * 0.5 + s2 * 0.5;
    color.b = (n + s1 + s2) * 0.333;

    // Add time-based color transformation
    vec3 hslColor = vec3(
        0.5 + sin(iTime * 0.5) / 4.0,   // Hue variation over time
        0.7 + n * 0.2,                  // Saturation
        0.5 + (s1 + s2) * 0.25          // Lightness
    );

    return vec4(hsl2rgb(hslColor), 1.0);
}
