vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Add time-based rotation
    theta += iTime * pi / 2.0;
    // Create hexagonal grid pattern
    float angle = mod(theta + pi/3.0, pi*2.0) - pi;
    float dist = r * cos(angle);

    // Use noise and turbulence for variations
    float noiseVal = turbulent(vec2(st.x, st.y)) * 0.5 + 0.5;
    float hexPattern = smoothstep(0.4, 0.6, abs(dist));

    // Create color transitions
    vec3 color = hsl2rgb(vec3(mod(iTime * 0.1, 1.0), 0.8, 0.5 + noiseVal * 0.3));

    // Add trailing effect
    float trail = exp(-iTime) * 0.5;
    color += vec3(turbulent(vec3(st.x, st.y, iTime)) * 0.2);

    return vec4(hexPattern * color + (1.0 - hexPattern) * trail, 1.0);
}
