vec4 Effect2(vec2 uv) {
    // Create a grid of squares using tiling pattern
    vec2 st = fract(uv * 100.0);

    // Calculate center point for each square
    vec2 center = fract(st - 0.5);

    // Calculate rotation angle based on position and time
    float speed = length(center) + turbulent(vec3(center, iTime)) * 0.5;
    float angle = atan2(center.y, center.x) + iTime * speed;

    // Apply rotation matrix to each square
    vec2 rotated = vec2(
        cos(angle) * (st.x - 0.5) - sin(angle) * (st.y - 0.5),
        sin(angle) * (st.x - 0.5) + cos(angle) * (st.y - 0.5)
    ) + 0.5;

    // Create square shape
    float square = max(abs(rotated.x - 0.5), abs(rotated.y - 0.5));

    // Add noise and color transformation
    vec3 color = hsl2rgb(vec3(
        length(center) * 0.7 + iTime * 0.1,
        0.8 + turbulent(vec3(st, iTime)) * 0.2,
        0.5 + sin(iTime + length(center)*10.0) * 0.5
    ));

    // Combine effects and output
    return vec4(color * (1.0 - square), 1.0);
}
