vec4 Effect2(vec2 uv) {
    // Create rotating grid pattern
    float time = iTime * 0.5;
    vec2 rotatedUV = uv * cos(time) + uv.yx * sin(time);

    // Add moving sine waves overlay
    float waveX = sin(rotatedUV.x * 8.0 + time * 2.0) * 0.5 + 0.5;
    float waveY = sin(rotatedUV.y * 8.0 - time * 2.0) * 0.5 + 0.5;

    // Combine grid and waves
    float combined = (waveX + waveY) * 0.5;

    // Create color transitions using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(time * 0.1, 1.0, 0.8));

    // Overlay effect with grid lines
    float grid = step(0.9, abs(sin(rotatedUV.x * 4.0 + time)) + 
                    abs(cos(rotatedUV.y * 4.0 - time)));

    return vec4(color * combined + (1.0 - combined) * vec3(grid), 1.0);
}
