vec4 Effect2(vec2 uv) {
    // Scale and offset UV coordinates
    vec2 st = uv * 10.0 - 5.0;

    // Create a time-based wave movement
    float time = iTime * 0.5;
    float wave = sin(st.x * 0.5 + time) * cos(st.y * 0.5 + time);

    // Add color shifting using HSL
    vec3 color = hsl2rgb(vec3(
        mod(time * 0.1, 1.0),  // Hue
        0.7,                  // Saturation
        0.5 + wave * 0.2      // Lightness with wave modulation
    ));

    // Add turbulence for more dynamic effect
    float turb = turbulent(vec3(st.x, st.y, time));
    color *= 1.0 + turb * 0.4;

    return vec4(color, 1.0);
}
