vec4 Effect2(vec2 uv) {
    // Create polar coordinates from UV
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Add pulsating effect
    float pulse = sin(iTime * 0.7) * 0.5 + 0.5;
    r *= 1.0 + pulse * 0.4;

    // Create color variations using turbulence and noise
    vec3 color = hsl2rgb(vec3(
        theta * 0.1,
        0.8 + turbulent(vec3(st, iTime)) * 0.2,
        0.5 + noise(vec2(r * 4.0, theta)) * 0.5
    ));

    // Add radial motion and blending
    color *= 1.0 - r * 0.6;
    color += vec3(turbulent(vec2(st * 0.5 + iTime * 0.2)));

    return vec4(color, 1.0);
}
