vec4 Effect2(vec2 uv) {
    vec4 fragColor;

    // Create rotating geometric pattern using polar coordinates
    float angle = atan2(uv.y, uv.x) + iTime * 0.25;
    float radius = length(uv);

    // Generate multiple overlapping patterns with sine functions
    float pattern1 = sin(angle * 6.0) * 0.5 + 0.5;
    float pattern2 = sin(radius * 30.0 - iTime * 2.0) * 0.5 + 0.5;

    // Add noise and turbulence for organic feel
    vec2 noiseUV = uv * 10.0 + iTime;
    float noisePattern = noise(noiseUV);
    float turbulentPattern = turbulent(vec3(uv, iTime));

    // Combine patterns with color transitions
    float combined = pattern1 * pattern2 + noisePattern * 0.5 + turbulentPattern * 0.3;
    combined = clamp(combined, 0.0, 1.0);

    // Convert to HSL and animate hue over time
    vec3 hslColor = vec3(0.5 + sin(iTime) * 0.5, 0.7, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    fragColor = vec4(combined * rgbColor, 1.0);

    return fragColor;
}
