vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Transform coordinates over time
    float scale = sin(iTime * 0.5) * 3.0 + 4.0;
    st *= scale;
    st = mat2(cos(iTime), -sin(iTime), sin(iTime), cos(iTime)) * st;

    // Add noise and turbulence
    vec3 point = vec3(st.x, st.y, iTime * 0.1);
    float t = turbulent(point) * 2.0 + noise(vec3(st, iTime));

    // Convert to HSL and then RGB
    vec3 color = hsl2rgb(vec3(
        t * 60.0 + iTime * 30.0,
        sin(t * 5.0 + iTime) * 0.5 + 0.5,
        cos(t * 7.0 + iTime * 2.0) * 0.5 + 0.5
    ));

    return vec4(color, 1.0);
}
