vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create rotating pattern using polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x) + time;
    float radius = length(st);

    // Generate geometric patterns with noise and turbulence
    float pattern = sin(theta * 4.0) * cos(theta * 6.0) +
                   sin(theta * 8.0) * cos(theta * 12.0);
    pattern = abs(pattern) * 0.5 + 0.5;

    // Create vibrant color transitions
    vec3 hslColor = vec3(
        fract(time * 0.2 + radius * 0.4),
        0.8,
        0.6 + turbulent(vec3(radius, time, 0.0)) * 0.1
    );

    // Convert to RGB and add dynamic shadows
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= 0.5 + 0.5 * noise(vec3(radius, time, 0.0));

    return vec4(rgbColor, 1.0);
}
