vec4 Effect2(vec2 uv) {
    // Create base terrain using fractal noise
    float terrain = turbulent(uv * 10.0 + iTime);

    // Add secondary layer of noise for more detail
    terrain += turbulent(uv * 5.0 + iTime * 0.5) * 0.5;

    // Create height-based color transitions
    vec3 color = hsl2rgb(vec3(
        mod(iTime * 0.1, 1.0),  // Hue that shifts over time
        0.7,                    // Saturation
        0.5 + terrain * 0.4     // Value based on terrain height
    ));

    // Add atmosphere effect using distance from center
    float dist = length(uv);
    color = mix(
        color,
        hsl2rgb(vec3(mod(iTime * 0.15, 1.0), 0.6, 0.8)),
        smoothstep(1.0, 0.0, dist)
    );

    // Add height-based color transitions
    color = mix(
        color,
        hsl2rgb(vec3(mod(iTime * 0.2 + uv.y * 0.5, 1.0), 0.8, 0.6)),
        smoothstep(0.5, 1.0, terrain)
    );

    return vec4(color, 1.0);
}
