vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x);
    // Animate rotation and add noise variation
    theta += iTime * 5.0 + turbulent(vec3(theta / 4.0, iTime * 0.5, 0.0)) * 2.0;

    // Create color layers with HSL
    vec3 color1 = hsl2rgb(vec3(theta * 0.15 + iTime * 0.1, 0.6, 0.8));
    vec3 color2 = hsl2rgb(vec3(theta * 0.15 + iTime * 0.1 + 0.5, 0.4, 0.7));
    // Mix colors based on radius and add glow
    float mixAmount = smoothstep(0.0, 1.0, r);
    vec3 finalColor = mix(color2, color1, mixAmount) * (1.0 - r * 0.5) + 
                     turbulent(vec2(theta / 4.0, iTime)) * 0.3 * (1.0 - r);
    return vec4(finalColor, 1.0);
}
