vec4 Effect2(vec2 uv) {
    // Animate coordinates with sine waves
    vec2 st = uv * 10.0 + vec2(sin(iTime), cos(2.0*iTime)) * 5.0;

    // Add noise and turbulence
    float turb = turbulent(st * 10.0 + iTime);

    // Create color shifts using HSL
    vec3 baseColor = hsl(
        sin(iTime * 0.5) * 0.5 + 0.5,   // Hue variation
        0.7,                              // Saturation
        cos(iTime * 0.3) * 0.5 + 0.5     // Lightness variation
    );

    // Add RGB shifts based on position and time
    vec3 rgbShift = vec3(
        sin(st.x * 2.0 + iTime),
        sin(st.y * 2.0 + iTime + 2.0),
        sin((st.x + st.y) * 1.5 + iTime)
    ) * 0.3;

    // Combine everything
    vec3 finalColor = mix(baseColor, rgbShift, turb);

    return vec4(finalColor, 1.0);
}
