vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for hexagonal symmetry
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create rotating pattern with time-based rotation
    float timeRotation = iTime * 0.5;
    a += timeRotation;
    // Hexagonal grid pattern using polar coordinates
    float hexPattern = turbulent(vec3(r, a, 0.0));

    // Color based on angle and radius
    vec3 color = hsl(a * 0.1 + iTime * 0.5, 0.7, 0.6);

    // Add pulsating effect to colors
    float pulse = sin(iTime * 2.0) * 0.5 + 0.5;
    color *= pulse;
    // Depth-based fog effect
    float fogFactor = smoothstep(1.0, 0.0, r);
    fogFactor = pow(fogFactor, 3.0); // Increase fog intensity

    // Combine pattern and color with fog
    vec3 finalColor = mix(vec3(0.0), color, hexPattern * fogFactor);
    return vec4(finalColor, 1.0);
}
