vec4 Effect2(vec2 uv) {
    // Create a base color using HSL with time-based modulation
    vec3 color = hsl(
        sin(uv.x * 10.0 + iTime) * 0.5 + 0.5,  // Hue variation across screen and time
        0.7,  // Saturation
        0.6  // Lightness
    );

    // Add flowing movement using sine waves modulated by time
    float flow = sin(uv.x * 10.0 + iTime) * 0.5;
    flow += sin(uv.y * 10.0 + iTime * 2.0) * 0.3;

    // Add turbulence for more dynamic movement
    vec2 noiseUV = uv * 10.0 + vec2(iTime, iTime);
    float turb = turbulent(noiseUV);

    // Combine color and movement
    color = hsl2rgb(vec3(
        (sin(uv.x * 5.0 + iTime) + sin(uv.y * 5.0 + iTime)) * 0.5 + 0.5,  // Dynamic hue
        0.7,
        turb * 0.4 + 0.6  // Modulate lightness with turbulence
    ));

    // Add pulsing effect to overall brightness
    float pulse = sin(iTime) * 0.3 + 0.7;
    color *= pulse;

    return vec4(color, 1.0);
}
