vec4 Effect2(vec2 uv) {
    // Animate parameters over time
    float time = iTime * 0.5;

    // Create a base grid pattern using sine waves
    vec2 st = uv * 10.0 + time;
    float grid = sin(st.x * PI) * cos(st.y * PI);

    // Add noise to create dynamic movement
    vec3 noisePos = vec3(uv * 5.0, time);
    float turbulence = turbulent(noisePos);

    // Create intersecting lines with varying thickness
    float line1 = sin(uv.x * PI + time) * 0.5 + 0.5;
    float line2 = cos(uv.y * PI - time) * 0.5 + 0.5;

    // Combine lines and add color
    vec3 color = hsl2rgb(vec3(
        mod(time, 1.0) * 360.0,
        0.7,
        line1 * line2 * (1.0 + turbulence * 0.5)
    ));

    // Add anti-aliasing and glow effect
    float aa = smoothstep(0.9, 1.0, grid);
    color *= mix(vec3(0.2), vec3(1.0), aa);

    return vec4(color, 1.0);
}
