vec4 Effect2(vec2 uv) {
    // Base circle field
    vec3 baseColor = vec3(0.0);

    // Generate random circles using noise
    for(float i = 0.0; i < 100.0; i++) {
        float seed = (i + 1.0) * 0.1;
        vec2 pos = fract(vec2(i, i) * 0.3) + noise(vec2(seed, seed)) * 0.5;
        float radius = 0.05 + noise(vec2(seed, seed + 1.0)) * 0.05;

        // Circle equation
        vec2 toCircle = uv - pos;
        float dist = length(toCircle);
        float circle = smoothstep(radius, 0.0, dist);

        // Add color with varying hue
        baseColor += hsl2rgb(vec3(iTime * 0.1 + i * 0.1, 0.7, 0.5)) * circle;
    }

    // Moving sine wave overlay
    vec2 waveUV = uv * 4.0 + iTime;
    float wave1 = sin(waveUV.x * pi + iTime) * 0.5 + 0.5;
    float wave2 = sin(waveUV.y * pi + iTime * 0.7) * 0.5 + 0.5;

    // Combine waves and add turbulence
    vec3 waveColor = hsl2rgb(vec3(iTime * 0.1, 0.8, 0.6));
    float waveEffect = (wave1 + wave2) * 0.5;
    waveEffect *= turbulent(uv * 4.0 + iTime);

    // Combine circle field with sine waves
    vec3 finalColor = baseColor * (1.0 - waveEffect) + waveColor * waveEffect;

    return vec4(finalColor, 1.0);
}
