vec4 Effect2(vec2 uv) {
    // Create pulsating sine wave pattern
    float wave = sin(uv.x * 4.0 + iTime) * 0.5 + 0.5;
    wave += sin(iTime * 0.5 + uv.x * 8.0) * 0.3;

    // Create shifting RGB colors using HSL
    vec3 color = hsl2rgb(vec3(0.5 * iTime, 1.0, wave));

    // Add vertical movement and noise
    float yWave = sin(uv.y * 4.0 + iTime * 0.7) * 0.3;
    color *= vec3(wave + yWave);

    return vec4(color, 1.0);
}
