vec4 Effect2(vec2 uv) {
    // Create a grid of hexagon centers
    vec2 st = floor(uv * 10.0);

    // Convert to polar coordinates for hexagonal symmetry
    vec2 pos = vec2(
        cos(atan2(st.y, st.x)),
        sin(atan2(st.y, st.x))
    );

    // Calculate distance from center with hexagonal pattern
    float dist = length(uv - st);
    float angle = atan2(pos.y, pos.x) + iTime * 0.25; // Slow rotation

    // Pulsating glow effect
    float pulse = sin(iTime * 3.0 + dist * 10.0) * 0.5 + 0.5;

    // Concentric circles around each hexagon
    float rings = sin(length(uv - st) * 20.0 + iTime * 2.0);

    // Color transitions using HSL
    vec3 color1 = hsl2rgb(vec3(
        (angle + dist) * 0.5,
        0.8,
        pulse * 0.7 + 0.3
    ));

    vec3 color2 = hsl2rgb(vec3(
        (angle - dist) * 0.5,
        0.6,
        rings * 0.4 + 0.6
    ));

    // Combine effects with noise modulation
    float noiseMod = noise(vec3(st, iTime)) * 0.5 + 0.5;
    vec3 finalColor = mix(color1, color2, noiseMod);

    return vec4(finalColor, 1.0);
}
