vec4 Effect2(vec2 uv) {
    // Rotate UV coordinates over time
    float angle = sin(iTime * 0.5) * pi;
    vec2 rotatedUV = vec2(
        (uv.x - 0.5) * cos(angle) - (uv.y - 0.5) * sin(angle),
        (uv.x - 0.5) * sin(angle) + (uv.y - 0.5) * cos(angle)
    ) + 0.5;

    // Create noise patterns with time offset
    vec2 st = rotatedUV * 8.0 + iTime;
    float n1 = noise(st);
    float n2 = noise(st + vec2(0.3, 0.7));

    // Mix noise patterns with turbulence
    float pattern = (n1 + n2) * 0.5;
    pattern += turbulent(rotatedUV * 4.0 + iTime * 0.5);

    // Create dynamic color shifts using HSL
    vec3 color = hsl(
        sin(iTime * 0.2 + rotatedUV.x * pi) * 0.5 + 0.5,
        0.7 + sin(rotatedUV.y * pi + iTime) * 0.3,
        0.5 + pattern * 0.4
    );

    // Add color transformation and contrast
    color = hsv2rgb(vec3(
        color.r * 0.8 + 0.2,
        color.g * 1.2,
        color.b * 1.2
    ));

    return vec4(color, 1.0);
}
