vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);
    float angle = atan2(st.y, st.x) + iTime * 8.0;

    // Create layered sine patterns with time-based phase shifts
    float r = sin(radius * 8.0 + iTime) * 0.5 + 0.5;
    float g = sin(radius * 4.0 + iTime * 2.0 + angle) * 0.5 + 0.5;
    float b = sin(radius * 2.0 + iTime * 3.0 + angle * 2.0) * 0.5 + 0.5;

    // Add turbulence for dynamic plasma effect
    vec2 noiseUV = uv * 10.0 + iTime;
    float turb = turbulent(noiseUV);

    // Combine colors and add turbulence
    vec3 color = vec3(r, g, b) * (turb * 0.5 + 0.5);

    return vec4(color, 1.0);
}
