vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create multiple layers of rotating sine waves
    for(float i = 1.0; i <= 5.0; i++) {
        float speed = i * 0.5;
        float time = iTime * speed;

        // Calculate rotating pattern using sine waves
        vec2 st = uv * i + vec2(sin(time), cos(time)) * 0.5;
        float angle = atan2(st.y, st.x);
        float radius = length(st) * 10.0;

        // Create color cycling effect
        float hue = (radius - time) * 0.1;
        vec3 c = hsl(hue, 1.0, 0.5);

        // Add turbulence for dynamic patterns
        float turb = turbulent(vec2(uv.x * iTime, uv.y * iTime)) * 0.5 + 0.5;

        // Combine layers with different frequencies
        color += sin(radius - time) * c * turb;
    }

    // Add noise for extra detail
    color += noise(uv * 10.0 + iTime) * 0.3;

    return vec4(hsl2rgb(color), 1.0);
}
