vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation effect
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;
    // Create rotating sine wave pattern with increasing frequency
    float wave = sin(r * 5.0 + theta) * 0.5 + 0.5;

    // Add noise for texture and complexity
    vec3 noiseColor = vec3(noise(vec2(theta, r * 0.1)), 
                          noise(vec2(theta * 0.7, r * 0.2)),
                          noise(vec2(theta * 0.9, r * 0.3)));

    // Create dynamic color transitions using HSL
    vec3 baseColor = hsl2rgb(vec3(theta + iTime, 1.0, 0.5));

    // Combine wave pattern with noise and color transitions
    float mixAmount = wave * 0.7 + 0.3;
    vec3 finalColor = mix(baseColor, noiseColor * 0.8, mixAmount);

    return vec4(finalColor, 1.0);
}
