vec4 Effect2(vec2 uv) {
    vec3 finalColor = hsv2rgb(vec3(
        noise(uv * 5.0 + iTime * 0.1),
        0.8,
        0.8
    ));

    float timeFactor = iTime * 0.2;
    vec2 st = uv * 2.0 - 1.0;
    st.x += sin(timeFactor) * 0.5;
    st.y += cos(timeFactor * 0.7) * 0.5;

    float noiseValue = noise(st * 3.0 + timeFactor);
    finalColor *= vec3(0.8 + noiseValue * 0.2);

    float distanceFromCenter = length(uv - 0.5);
    finalColor += vec3(0.2, 0.4, 0.6) * (1.0 - smoothstep(0.0, 1.0, distanceFromCenter));

    return vec4(finalColor, 1.0);
}
