vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to range [-1.0, 1.0]
    vec2 pos = uv * 2.0 - 1.0;

    // Add time-based rotation
    float angle = atan(pos.y, pos.x) + iTime * 0.5;

    // Calculate polar coordinates
    float radius = length(pos);
    float theta = fract(angle / (pi * 2.0)) * pi * 2.0;

    // Create hexagon pattern using polar coordinates
    float hex = abs(cos(theta * 3.0)) * radius;
    hex = smoothstep(0.5, 1.0, hex);

    // Add turbulence for gradient effect
    vec3 color = vec3(turbulent(vec2(radius, theta + iTime)));

    // Convert to HSL and animate hue over time
    vec3 hslColor = vec3(fract(iTime * 0.2), 1.0, 0.5);
    color = hsl2rgb(hslColor);

    return vec4(color, 1.0);
}
