vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create rotating color wheels using HSL colors
    float hue = fract(theta / (pi * 2.0) + iTime * 0.1); // Time-based color shift
    vec3 hslColor = vec3(hue, 0.7, 0.5);

    // Add pulsating rings effect
    float ring = sin(r * 8.0 - iTime * 2.0) * 0.5 + 0.5;
    hslColor.z *= ring; // Modulate lightness for pulsation

    // Apply noise texture for additional variation
    vec3 noiseTex = texture(iChannel0, st * 16.0).rgb;
    hslColor.g += noiseTex.r * 0.2; // Add noise to saturation

    // Convert HSL color to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
