vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 center = vec2(0.5);
    vec2 offset = uv - center;
    float radius = length(offset);
    float angle = atan2(offset.y, offset.x);

    // Create expanding circles using time and radius
    float circle = sin(radius * 10.0 + iTime) * 0.5 + 0.5;

    // Generate color based on polar coordinates
    vec3 colorBase = hsl(
        (radius * 2.0 + iTime) * 4.0,   // Hue: varies with radius and time
        0.7,                             // Saturation
        0.5 + circle * 0.5               // Lightness: modulated by circle pattern
    );

    // Add noise to create more dynamic color shifts
    float noiseValue = noise(vec2(radius * 10.0, iTime)) * 0.3;
    vec3 finalColor = hsl2rgb(
        vec3(
            (radius * 2.0 + iTime) * 4.0,
            0.7 + noiseValue * 0.3,
            0.5 + circle * 0.5
        )
    );

    return vec4(finalColor, 1.0);
}
