vec4 Effect2(vec2 uv) {
    // Rotate coordinates around center
    vec2 st = uv * 2.0 - 1.0;
    float angle = iTime * 0.5;
    st = vec2(
        cos(angle)*st.x + sin(angle)*st.y,
        -sin(angle)*st.x + cos(angle)*st.y
    );
    // Layered noise with different scales and rotations
    float n1 = turbulent(st * 4.0) * 0.5;
    float n2 = turbulent((st * 8.0 + iTime) * 0.5) * 0.3;
    float n3 = turbulent((st * 16.0 - iTime) * 0.25) * 0.2;
    // Combine noise layers with time-based modulation
    float value = sin(n1 + iTime) * 0.5 +
                  cos(n2 + iTime*0.5) * 0.3 +
                  sin(n3 - iTime*0.7) * 0.2;

    // Create color shifts using HSL to RGB conversion
    vec3 color = hsl(
        fract(iTime * 0.1 + value * 0.5) * PI,
        0.8 + abs(sin(iTime * 0.3)) * 0.2,
        0.4 + abs(cos(iTime * 0.2 + value * PI)) * 0.6
    );
    return vec4(color, 1.0);
}
