vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Particle parameters
    float particleScale = 10.0;
    float speed = 1.5;
    float noiseScale = 0.5;

    // Generate grid of particles
    vec2 st = uv * particleScale;
    vec2 cell = floor(st);
    vec2 center = (st - cell) * 0.9 + 0.05;

    // Particle position over time
    float t = iTime * speed;
    float phase = noise(vec3(cell, t)) * 10.0;
    vec2 pos = center + vec2(0.0, t + phase);

    // Add some horizontal movement using noise
    pos.x += sin(t * 0.5 + cell.x) * 0.2;
    pos.y += turbulent(vec3(pos.xy * noiseScale, t)) * 0.1;

    // Calculate velocity
    vec2 velocity = vec2(0.0, speed);
    float speedFactor = length(velocity);

    // Color based on velocity
    vec3 hsvColor = vec3(speedFactor * 0.6 + 0.3, 0.7, 0.8);
    vec3 rgbColor = hsv2rgb(hsvColor);

    // Draw particles with fading
    float dist = length(pos - center);
    float alpha = 1.0 - smoothstep(0.0, 0.5, dist);

    color += rgbColor * alpha;

    return vec4(color, 1.0);
}
