vec4 Effect2(vec2 uv) {
    // Create rotating pattern with time-based transformation
    vec2 center = vec2(0.5, 0.5);
    float angle = iTime * 0.1;

    // Transform UV coordinates with rotation and scaling
    vec2 transformedUV = (uv - center) * mat2(cos(angle), sin(angle), -sin(angle), cos(angle)) + center;

    // Add some distortion using sine waves
    transformedUV.x += sin(transformedUV.y * 4.0 * PI + iTime) * 0.3;
    transformedUV.y += sin(transformedUV.x * 8.0 * PI + iTime) * 0.2;

    // Calculate distance from center for color transitions
    float dist = length(transformedUV - center);

    // Create smooth color transitions using HSL
    vec3 hslColor = vec3(
        (dist * 2.0 + iTime) * PI,   // Hue
        0.7 + noise(uv * 4.0),       // Saturation with some noise variation
        0.5 + turbulent(transformedUV * 8.0) * 0.3  // Lightness with turbulence
    );

    // Convert HSL to RGB and add noise for texture
    vec3 color = hsl2rgb(hslColor);
    color *= 1.0 + noise(uv * 4.0 + iTime) * 0.5;

    return vec4(color, 1.0);
}
