vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;
    vec3 color = vec3(0.0);

    // Orb parameters
    float scale = 10.0;
    float speed = 0.75;
    int orbs = 8;

    for(int i = 0; i < orbs; i++) {
        // Generate random position using noise
        vec2 pos = fract(vec2(float(i) * 0.3, time * speed)) * 2.0 - 1.0;
        pos *= scale;

        // Calculate distance from current uv to orb center
        float dist = length(uv - pos);
        float radius = 0.5 + 0.5 * sin(time + float(i) * 0.3);

        // Create glowing effect with turbulence
        float glow = turbulent(vec2(dist, time)) * 0.5 + 0.5;
        glow = smoothstep(radius - 0.1, radius + 0.1, dist);

        // Color based on position and time
        vec3 orbColor = hsl2rgb(vec3(
            fract(time * 0.1 + float(i) * 0.2) * PI,
            0.7,
            0.5 + 0.5 * sin(time + uv.x * 2.0)
        ));

        // Add orb to color
        color += glow * orbColor;
    }

    // Add shimmering trail effect
    vec2 trailUV = uv * scale + time;
    float trail = turbulent(trailUV) * 0.5 + 0.5;
    color *= mix(vec3(1.0), hsl2rgb(vec3(time * PI, 1.0, 1.0)), trail * 0.5);

    return vec4(color, 1.0);
}
