vec4 Effect2(vec2 uv) {
    // Create a grid pattern by scaling and tiling the UV coordinates
    vec2 st = fract(uv * 10.0);

    // Add rotation effect using time
    float angle = sin(iTime * 0.5 + 3.1415926) * 0.5;
    mat2 rotMatrix = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotatedSt = rotMatrix * (st - 0.5) + 0.5;

    // Create square pattern using minimum distance to grid lines
    float squarePattern = min(rotatedSt.x, rotatedSt.y);
    squarePattern = min(squarePattern, 1.0 - rotatedSt.x);
    squarePattern = min(squarePattern, 1.0 - rotatedSt.y);

    // Generate dynamic color based on time and position
    vec3 color = hsl(
        sin(iTime * 0.2 + rotatedSt.x * 5.0) * 0.5 + 0.5,
        0.7,
        0.6
    );

    // Add noise to create dynamic variations
    float noiseValue = noise(vec3(rotatedSt, iTime));
    color *= 1.0 + noiseValue * 0.2;

    // Combine pattern and color for final output
    return vec4(color * squarePattern, 1.0);
}
