vec4 Effect2(vec2 uv) {
    // Create mirrored patterns by repeating and reflecting coordinates
    float mirrors = 8.0;
    vec2 mirroredUv = mod(uv * mirrors, 1.0);

    // Reflect every other section
    mirroredUv.x = mirroredUv.x - (mod(floor(mirroredUv.x), 2.0) == 1.0 ? 1.0 : 0.0);
    mirroredUv.y = mirroredUv.y - (mod(floor(mirroredUv.y), 2.0) == 1.0 ? 1.0 : 0.0);

    // Add some turbulence to create dynamic patterns
    vec3 noiseInput = vec3(mirroredUv * 5.0, iTime * 0.5);
    float turb = turbulent(noiseInput);

    // Create shifting hues based on position and time
    vec3 colorHSL;
    colorHSL.x = (mirroredUv.x + mirroredUv.y) * 0.5 + iTime * 0.1;
    colorHSL.y = 0.7 + turb * 0.2; // Add some variation in saturation
    colorHSL.z = 0.5 + turb * 0.3; // Add some variation in lightness

    vec3 finalColor = hsl2rgb(colorHSL);

    return vec4(finalColor, 1.0);
}
