vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates for spiral effect
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st) * 3.0;

    // Add time-based rotation and radial movement
    theta += iTime * 0.5;
    r += sin(theta + iTime) * 0.5;

    // Create spiral pattern using HSL colors
    vec3 baseColor = hsl(theta * 0.5 + iTime, 1.0, 0.5);
    float noiseValue = noise(vec2(r, theta)) * 0.5 + 0.5;

    // Mix in noise for texture and variation
    vec3 finalColor = mix(baseColor, hsl(theta * 0.5 + iTime + noiseValue, 1.0, 0.6), 0.2);

    return vec4(finalColor, 1.0);
}
