vec4 Effect2(vec2 uv) {
    // Normalize UV coordinates
    vec2 st = (uv + 1.0) * 0.5;

    // Create polar coordinates
    float radius = length(st - 0.5);
    float angle = atan2(st.y - 0.5, st.x - 0.5);

    // Animate rotation
    angle += iTime * 0.1;

    // Hexagon pattern
    float hex = sin(6.0 * angle) * 0.5 + 0.5;
    float pattern = radius * 2.0 < hex ? 1.0 : 0.0;

    // Cellular noise overlay
    vec2 noiseUV = st * 4.0; // Scale noise
    float cellNoise = turbulent(noiseUV);

    // Combine patterns and add time-based color
    float finalPattern = pattern * 0.7 + cellNoise * 0.3;
    vec3 color = hsl2rgb(vec3(sin(iTime) * 0.5 + 0.5, 1.0, finalPattern));

    return vec4(color, 1.0);
}
