vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Time-based transformation
    st.x += sin(iTime) * 0.5;
    st.y += cos(iTime * 1.5);

    // Generate noise and turbulence patterns
    float n = noise(st * 3.1415926);
    float t = turbulent(vec2(uv * 5.)) * 0.2;

    // Combine noise and turbulence with time
    vec3 colorHSL = hsl(n * 0.5 + 0.5, 
                       sin(iTime) * 0.5 + 0.5,
                       cos(iTime * 0.5) * 0.5 + 0.5);

    // Mix colors with time-based factors
    vec3 finalColor = mix(hsl2rgb(colorHSL), hsv2rgb(vec3(sin(n * PI) * 0.5 + 0.5, t * 0.5 + 0.5, cos(iTime) * 0.5 + 0.5)), 
                        sin(iTime * 0.7) * 0.5 + 0.5);

    // Apply final scaling and mix
    return vec4(finalColor * (1.0 - abs(sin(st.x * PI)) * 0.2), 1.0);
}
