vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for checkerboard pattern
    vec2 st = uv * 8.0;

    // Add time-based movement to the pattern
    st += sin(iTime * 0.5) * 2.0;

    // Create a grid of cells using floor and mod functions
    vec2 cell = floor(st);

    // Determine if current cell is black or colored based on checkerboard pattern
    float isBlack = mod(cell.x + cell.y, 2.0);

    // Calculate color for non-black cells with RGB shifts
    vec3 color;
    if (isBlack < 0.5) {
        // Use HSL colors with time-based hue shift
        float hue = fract(iTime * 0.1) * 360.0;
        color = hsl2rgb(vec3(hue, 0.7, 0.8));

        // Add noise to create variations in color intensity
        vec2 noisePos = cell.xy * 0.5 + iTime;
        float n = noise(noisePos) * 0.4 + 0.6;
        color *= n;
    } else {
        // Black cells
        color = vec3(0.0);
    }

    return vec4(color, 1.0);
}
