vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float a = atan2(uv.y, uv.x);
    float r = length(uv);

    // Create rotating sine wave pattern
    float timeMod = iTime * 0.5;
    float sinVal = sin(r * 10.0 + timeMod) * 0.5 + 0.5;
    float sinRot = sin(a + timeMod) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        (a / PI + 0.5 + timeMod * 0.1) * 360.0, 
        0.7,
        sinVal * 0.5 + 0.5
    );

    // Convert to RGB and mix with sine rotation
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= sinRot;

    // Add noise for shimmer effect
    vec2 noiseUV = uv * 10.0 + iTime;
    float noiseVal = noise(noiseUV) * 0.5 + 0.5;
    rgbColor = mix(rgbColor, hsl2rgb(vec3((a / PI + timeMod) * 360.0, 1.0, 1.0)), noiseVal);

    return vec4(rgbColor, 1.0);
}
