vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5; // Speed control

    // Rotate coordinates
    vec2 rotatedUv;
    rotatedUv.x = uv.x * cos(time) - uv.y * sin(time);
    rotatedUv.y = uv.x * sin(time) + uv.y * cos(time);

    // Add sine wave distortion
    vec2 distortedUv = rotatedUv;
    distortedUv.x += sin(rotatedUv.y * 4.0 + time * 2.0) * 0.3;
    distortedUv.y += sin(rotatedUv.x * 4.0 + time * 1.5) * 0.3;

    // Add turbulence
    vec2 finalUv = distortedUv * 0.5 + 0.5;
    float t = turbulent(finalUv * 8.0);

    // Color calculation with RGB shifts
    vec3 color = hsl(
        sin(time * 0.7) * 0.5 + 0.5, // Hue variation
        1.0 - abs(turbulent(finalUv * 4.0)), // Saturation
        0.5 // Lightness
    );

    return vec4(color, 1.0);
}
