vec4 Effect2(vec2 uv) {
    // Create a grid of points scaled by UV coordinates
    vec2 st = uv * 10.0;

    // Convert to polar coordinates
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 1.5; // Add time-based rotation

    // Create a triangular pattern using sine wave
    float triangle = sin(theta * 3.0 + iTime) * 0.5 + 0.5;

    // Create a mask for the triangles
    float mask = smoothstep(0.7, 1.0, abs(triangle));

    // Add color shifts using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        (theta * 0.15 + iTime) * 360.0,
        0.8,
        0.7
    ));

    // Mix in some noise for extra variation
    float n = noise(vec2(r, theta)) * 0.1;
    mask += n;

    // Final color output
    return vec4(color * mask, 1.0);
}
