vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan(st.y, st.x);
    float radius = length(st);

    // Add time-based rotation
    theta += iTime * 0.5;

    // Create rotating geometric pattern using sine waves
    vec3 color = hsl2rgb(vec3(
        sin(theta) * 0.5 + 0.5,   // Hue variation
        1.0,                      // Full saturation
        0.5                       // Mid lightness
    ));

    // Add pulsating effect based on time and radius
    float pulse = sin(iTime * 2.0 + radius * 8.0) * 0.5 + 0.5;
    color *= pulse;

    // Add noise texture with dynamic scaling
    vec3 noise1 = vec3(noise(vec2(theta, iTime)));
    vec3 noise2 = vec3(noise(vec2(radius * 50.0, iTime * 2.0)));
    color += (noise1 + noise2) * 0.3;

    // Add geometric interference pattern
    float pattern = sin(theta * 4.0) * cos(radius * 20.0);
    color += vec3(pattern * 0.5);

    return vec4(color, 1.0);
}
