vec4 Effect2(vec2 uv) {
    // Rotate coordinates based on time
    float angle = sin(iTime * 0.5) + 1.0;
    vec2 rotatedUv = vec2(
        uv.x * cos(angle) - uv.y * sin(angle),
        uv.x * sin(angle) + uv.y * cos(angle)
    ) * 2.0;
    // Create a base noise pattern with time offset
    float noise1 = noise(vec3(rotatedUv * 100.0, iTime));
    float noise2 = noise(vec3(rotatedUv * 50.0 + vec2(123.456), iTime * 0.7));
    // Create turbulence pattern
    float turb = turbulent(rotatedUv * 10.0);

    // Combine patterns with color transformations
    vec3 color1 = hsl2rgb(vec3(
        mod(iTime + noise1, 1.0) * 360.0,
        0.7,
        0.5 + turb * 0.5
    ));

    vec3 color2 = hsl2rgb(vec3(
        mod(iTime + noise2, 1.0) * 360.0,
        0.8,
        0.4 + abs(sin(iTime)) * 0.6
    ));
    // Mix colors based on patterns
    vec3 finalColor = mix(color1, color2, smoothstep(0.0, 1.0, noise(rotatedUv)));

    return vec4(finalColor, 1.0);
}
