vec4 Effect2(vec2 uv) {
    // Rotate coordinates based on time
    vec2 center = vec2(0.5);
    float angle = iTime * 0.1;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotatedUV = (uv - center) * rot + center;
    // Create radial waves pattern
    vec2 uv_centered = rotatedUV - center;
    float dist = length(uv_centered);

    // Multiple wave frequencies with time-based animation
    float wave1 = sin(dist * 8.0 + iTime * 3.0) * 0.5;
    float wave2 = sin(dist * 16.0 + iTime * 4.0) * 0.3;
    float wave3 = sin(dist * 32.0 + iTime * 5.0) * 0.2;

    // Combine waves and add turbulence
    float value = (wave1 + wave2 + wave3) * 0.5;
    value += turbulent(rotatedUV * 4.0) * 0.2;
    // Convert to HSL color with smooth transitions
    vec3 hslColor = vec3(
        mod(value * 6.0 + iTime, 1.0),  // Hue
        0.7 + abs(sin(iTime * 0.5)) * 0.3,  // Saturation
        0.5 + value * 0.4  // Lightness
    );

    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
