vec4 Effect2(vec2 uv) {
    // Create rotating pattern with time-based movement
    vec2 st = uv * 2.0 - 1.0;
    float angle = iTime * 0.5;
    mat2 rot = mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
    vec2 rotatedUV = rot * st;
    // Add turbulence and noise layers
    float t = turbulent(rotatedUV * 4.0 + iTime);
    float n1 = noise(rotatedUV * 8.0 + iTime) * 0.5;
    float n2 = noise(rotatedUV * 16.0 + iTime + 1.0) * 0.3;

    // Create color shifts using HSL
    vec3 hslColor = vec3(
        mod(iTime * 0.1 + rotatedUV.x * 0.5, 1.0),
        0.7,
        0.6 + t * 0.2
    );

    // Combine noise layers and colors
    float finalNoise = (n1 + n2) * 0.8;
    vec3 color = hsl2rgb(hslColor);
    color *= sin(iTime * 0.5 + rotatedUV.y * 4.0) * 0.5 + 0.5;

    // Add geometric pattern overlay
    float pattern = abs(sin(rotatedUV.x * 8.0)) * abs(cos(rotatedUV.y * 8.0));
    color = mix(color, vec3(1.0), pattern * 0.3);

    return vec4(color, 1.0);
}
