vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);
    // Create interweaving sine patterns with dynamic scaling
    float scale = sin(iTime) * 0.5 + 0.5;
    float pattern1 = sin(theta * 3.0 - iTime) * scale;
    float pattern2 = sin(theta * 5.0 + iTime) * scale;
    // Combine patterns and create color
    float value = (sin(r * 4.0 + pattern1 * 2.0) + sin(r * 6.0 + pattern2 * 2.0)) / 2.0;

    // Create shifting hues using HSL
    float hue = iTime * 2.0 + theta * 3.0;
    vec3 color = hsl2rgb(vec3(hue, 1.0, 0.5 + sin(iTime) * 0.5));
    return vec4(color * (value * 0.5 + 0.5), 1.0);
}
