vec4 Effect2(vec2 uv) {
    // Center and rotate coordinates
    vec2 st = uv - 0.5;
    float time = iTime * 0.5;

    // Apply rotation
    st = mat2(cos(time), sin(-time),
              sin(time), cos(time)) * st;
    // Create hexagonal pattern using polar coordinates
    vec2 polar = vec2(length(st), atan2(st.y, st.x));
    float r = polar.x;
    float theta = polar.y;

    // Generate geometric pattern with noise
    float pattern = cos(theta * 6.0 + time) * 0.5 + 0.5;
    pattern += cos(theta * 3.0 + time * 2.0) * 0.3;
    pattern = smoothstep(0.4, 0.6, pattern);

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        fract(time * 0.1 + theta / (2.0 * PI)) * 360.0,
        0.7,
        0.5 + 0.5 * sin(time + r * 4.0)
    );

    // Convert to RGB and add noise
    vec3 color = hsl2rgb(hslColor) * (0.5 + 0.5 * noise(st * 10.0 + time));

    // Add pulsing outer ring
    float pulse = smoothstep(0.8, 1.0, r);
    color *= 1.0 - pulse;

    return vec4(color, 1.0);
}
